import React, {Component} from 'react';
import styles from './Contacts.module.css';
import Title from "../BlockTitles/Title";
import Fade from "react-reveal/Fade";

class Contacts extends Component {

    constructor (props) {
        super(props);
        this.submitForm=this.submitForm.bind(this)
    }
    state = {status: ''};

    // getName = (e) => {
    //     this.setState({
    //         name: e.currentTarget.value
    //     })
    // };
    // getMail = (e) => {
    //     this.setState({
    //         mail: e.currentTarget.value
    //
    //     })
    // };
    // getMessage = (e) => {
    //     this.setState({
    //         message: e.currentTarget.value
    //     })
    // };

    submitForm(ev) {
        ev.preventDefault();
        const form = ev.target;
        const data = new FormData(form);
        const xhr = new XMLHttpRequest();
        xhr.open(form.method, form.action);
        xhr.setRequestHeader("Accept", "application/json");
        xhr.onreadystatechange = () => {
            if (xhr.readyState !== XMLHttpRequest.DONE) return;
            if (xhr.status === 200) {
                form.reset();
                this.setState({ status: "SUCCESS" });
            } else {
                this.setState({ status: "ERROR" });
            }
        };
        xhr.send(data);
    }

    render() {
        let title = 'Contacts';
        return (
            <div id={'contacts'} className={styles.contacts}>
                <Fade bottom>
                    <div className={styles.container}>
                        <Title title={title}/>
                        <form className={styles.formWrapper} name="simple-contact-form"
                              action='https://formspree.io/mzbzpwqz' method='POST' id='fs-frm' acceptCharset='utf-8'>
                            <input className={styles.inputForm} type='text' name='name' placeholder="Name"
                                   onChange={this.getName}
                                   required=''/>
                            <input className={styles.inputForm} type='email' placeholder="Email"
                                   onChange={this.getMail} required=''
                            />
                            <textarea className={styles.message} placeholder="Message" id="message" rows='5'
                                      name='message' onChange={this.getMessage} required=''/>
                            <div className={styles.posButton}>
                                <button className={styles.buttonSend} type="submit" value='submit'
                                        onClick={this.sendMessage}>Send
                                    message
                                </button>
                            </div>
                        </form>
                    </div>
                </Fade>
            </div>
        );
    }
}

export default Contacts;
