import React, {Component} from 'react';
import styles from './Contacts.module.css';
import Title from "../BlockTitles/Title";
import Fade from "react-reveal/Fade";

class Contacts extends Component {

    // constructor(props) {
    //     super(props);
    //     this.submitForm = this.submitForm.bind(this)
    // }

    state = {status: ''};

    submitForm = (ev) => {
        ev.preventDefault();
        const form = ev.target;
        const data = new FormData(form);
        const xhr = new XMLHttpRequest();
        xhr.open(form.method, form.action);
        xhr.setRequestHeader("Accept", "application/json");
        xhr.onreadystatechange = () => {
            if (xhr.readyState !== XMLHttpRequest.DONE) return;
            if (xhr.status === 200) {
                form.reset();
                this.setState({status: "SUCCESS"});
            } else {
                this.setState({status: "ERROR"});
            }
        };
        xhr.send(data);
    }

    render() {
        let title = 'Contacts';
        const status = this.state;
        return (
            <div id={'contacts'} className={styles.contacts}>
                <Fade bottom>
                    <div className={styles.container}>
                        <Title title={title}/>
                        <form className={styles.formWrapper} name="simple-contact-form"
                              action='https://formspree.io/mzbzpwqz' method='POST' id='fs-frm' acceptCharset='utf-8'
                              onSubmit={this.submitForm}>
                            <input className={styles.inputForm} type='text' name='name' placeholder="Name" required=''/>
                            <input className={styles.inputForm} type='email' placeholder="Email" required=''/>
                            <textarea className={styles.message} placeholder="Message" id="message" rows='5'
                                      name='message' required=''/>
                            <div className={styles.posButton}>
                                {status === 'SUCCESS' ? <p>Thaks!</p> :
                                    <button className={styles.buttonSend} type="submit" value='submit'>Send
                                        message</button>}
                                {status === 'ERROR' && <p>Ooops! There was an error</p>}
                            </div>
                        </form>
                    </div>
                </Fade>
            </div>
        );
    }
}

export default Contacts;
