import React, {Component} from 'react';
import styles from './Contacts.module.css';
import Title from "../BlockTitles/Title";
import Fade from "react-reveal/Fade";

class Contacts extends React.Component {

    constructor(props) {
        super(props);
        this.submitForm = this.submitForm.bind(this)
    }

    state = {status: ''};

    submitForm(ev) {
        ev.preventDefault();
        const form = ev.target;
        const data = new FormData(form);
        const xhr = new XMLHttpRequest();
        xhr.open(form.method, form.action);
        xhr.setRequestHeader("Accept", "application/json");
        xhr.onreadystatechange = () => {
            if (xhr.readyState !== XMLHttpRequest.DONE) return;
            if (xhr.status === 200) {
                form.reset();
                this.setState({status: "SUCCESS"});
            } else {
                this.setState({status: "ERROR"});
            }
        };
        xhr.send(data);
    }

    render() {
        let title = 'Contacts';
        const {status} = this.state;
        return (
            <div id={'contacts'} className={styles.contacts}>
                <Fade bottom>
                    <div className={styles.container}>
                        <Title title={title}/>
                        <form className={styles.formWrapper}
                              onSubmit={this.submitForm}
                              action="https://formspree.io/mnqnayrv"
                              method="POST"
                        >

                            <input className={styles.inputForm} type="name" name="name"/>
                            <input className={styles.inputForm} type="email" name="email"/>
                            <textarea className={styles.message} placeholder="Message" rows='5' name='message'/>
                            {status === "SUCCESS" ? <p>Thanks!</p> : <button>Submit</button>}
                            {status === "ERROR" && <p>Ooops! There was an error.</p>}
                        </form>
