import React, {Component} from 'react';
import styles from './Contacts.module.css';
import Title from "../BlockTitles/Title";
import Fade from "react-reveal/Fade";
import {Mailer} from 'nodemailer-react'

class Contacts extends Component {
    state = {
        name: '',
        mail: '',
        message: '',
    };

    getName = (e) => {
        this.setState({
            name: e.currentTarget.value
        })
    };
    getMail = (e) => {
        this.setState({
            mail: e.currentTarget.value

        })
    };
    getMessage = (e) => {
        this.setState({
            message: e.currentTarget.value
        })
    };

    sendMessage = async () => {
        const mailerConfig = {
            //     // defaults: {
            //     //     from: {
            //     //         name: 'mathieut',
            //     //         address: 'dev@mathieutu.ovh',
            //     //     },
            //     // },
            transport: {
                host: 'smtp.gmail.com',
                secure: true,
                auth: {
                    user: 'username',
                    pass: 'password',
                },
            },
        };
        const emailsList = {
            pass: "Password",
        };


        const info = {
            firstName: 'Mathieu',
            newAccount: true,
            // password: Math.random().toString(36).substring(7),
        };
        //
        // const mailer = Mailer(mailerConfig, emailsList);
        //
        // await mailer.send('pass', info, {
        //     to: 'foo@bar.fr',
        //     attachments: [{content: 'bar', filename: 'foo.txt'}],
        // });

        await this.setState({
            name: '',
            mail: '',
            message: ''
        })
    };

    render() {
        let title = 'Contacts';
        return (
            <div id={'contacts'} className={styles.contacts}>
                <Fade bottom>
                    <div className={styles.container}>
                        <Title title={title}/>
                        <form className={styles.formWrapper}>
                            <input className={styles.inputForm} type='text' placeholder="Name" onChange={this.getName}
                                   required/>
                            <input className={styles.inputForm} type='email' placeholder="Email"
                                   onChange={this.getMail} required
                            />
                            <textarea className={styles.message} placeholder="Message" onChange={this.getMessage}/>
                            <div className={styles.posButton}>
                                <button className={styles.buttonSend} type="submit" onClick={this.sendMessage}>Send
                                    message
                                </button>
                            </div>
                        </form>
                    </div>
                </Fade>
            </div>
        );
    }
}

export default Contacts;
